mapper.select:
select
	t1.id                             ,
	t1.code                           ,
	t1.goods_id                       ,
	t1.goods_name                       ,
	t1.goods_price                    ,
	t1.goods_color                    ,
	t1.goods_size                     ,
	t1.buyer_id                       ,
	t1.buyer_name                     ,
	t1.remark                         ,
	t1.status                         ,
	t1.pay_time                       ,
	t1.cancel_time                    ,
	t1.org_id                         ,
	t1.dept_id                        ,
	t1.sys_id                         ,
	t1.secret_level                   ,
	t1.sys_application_id             ,
	t1.created_by                     ,
	t1.creation_date                  ,
	t1.last_updated_by                ,
	t1.last_update_date               ,
	t1.last_update_ip                 ,
	t1.version                        ,
	t1.attribute_01                   ,
	t1.attribute_02                   ,
	t1.attribute_03                   ,
	t1.attribute_04                   ,
	t1.attribute_05                   ,
	t1.attribute_06                   ,
	t1.attribute_07                   ,
	t1.attribute_08                   ,
	t1.attribute_09                   ,
	t1.attribute_10                   ,
	t1.attribute_11                   ,
	t1.attribute_12                   ,
	t1.attribute_13                   ,
	t1.attribute_14                   ,
	t1.attribute_15                   
from orders 	t1


			and t1.ID =#{id}


			and t1.CODE =#{code}


			and t1.GOODS_ID =#{goodsId}


			and t1.GOODS_NAME =#{goodsName}


			and t1.GOODS_PRICE =#{goodsPrice}


			and t1.GOODS_COLOR =#{goodsColor}


			and t1.GOODS_SIZE =#{goodsSize}


			and t1.BUYER_ID =#{buyerId}


			and t1.BUYER_NAME =#{buyerName}


			and t1.REMARK =#{remark}


			and t1.STATUS =#{status}


			and t1.PAY_TIME >= #{payTimeBegin}


			and t1.PAY_TIME <= #{payTimeEnd}


			and t1.CANCEL_TIME >= #{cancelTimeBegin}


			and t1.CANCEL_TIME <= #{cancelTimeEnd}


			and t1.ORG_ID =#{orgId}


			and t1.DEPT_ID =#{deptId}


			and t1.SYS_ID =#{sysId}


			and t1.SECRET_LEVEL =#{secretLevel}


			and t1.SYS_APPLICATION_ID =#{sysApplicationId}


			and t1.CREATED_BY =#{createdBy}


			and t1.CREATION_DATE =#{creationDate}


			and t1.LAST_UPDATED_BY =#{lastUpdatedBy}


			and t1.LAST_UPDATE_DATE =#{lastUpdateDate}


			and t1.LAST_UPDATE_IP =#{lastUpdateIp}


			and t1.VERSION =#{version}


			and t1.ATTRIBUTE_01 =#{attribute01}


			and t1.ATTRIBUTE_02 =#{attribute02}


			and t1.ATTRIBUTE_03 =#{attribute03}


			and t1.ATTRIBUTE_04 =#{attribute04}


			and t1.ATTRIBUTE_05 =#{attribute05}


			and t1.ATTRIBUTE_06 =#{attribute06}


			and t1.ATTRIBUTE_07 =#{attribute07}


			and t1.ATTRIBUTE_08 =#{attribute08}


			and t1.ATTRIBUTE_09 =#{attribute09}


			and t1.ATTRIBUTE_10 =#{attribute10}


			and t1.ATTRIBUTE_11 =#{attribute11}


			and t1.ATTRIBUTE_12 =#{attribute12}


			and t1.ATTRIBUTE_13 =#{attribute13}


			and t1.ATTRIBUTE_14 =#{attribute14}


			and t1.ATTRIBUTE_15 =#{attribute15}
